import React, { Component } from 'react';
import { Button, Form } from 'semantic-ui-react';

class ButtonExampleInverted extends Component {
    state = { name: '', submittedName: '' }

  handleChange = (e, { name, value }) => this.setState({ [name]: value })

  handleSubmit = () => {
    const { name} = this.state

    this.setState({ submittedName: name })
  }

  render() {
    const { name, submittedName } = this.state

    return (
      <div>
        <Form onSubmit={this.handleSubmit}>
          <Form.Group>
            <Form.Input
              placeholder='Name'
              name='name'
              value={name}
              onChange={this.handleChange}
            />
                <Form.Button content='Submit' />
                <Form.Button inverted color= {submittedName}>
                selected color
                </Form.Button>
            </Form.Group>
        </Form>           
                
            
        </div>
        )
    }
}
export default ButtonExampleInverted;
