import React, { Component } from 'react'
import { Button, Segment, Form } from 'semantic-ui-react';

class ButtonExampleInverted extends Component {
    state = { color: '', submittedColor: '' }
    handleChange = (e, { color, value }) => this.setState({ [color]: value })

    handleSubmit = () => {
        const { color } = this.state

        this.setState({ submittedColor: color })
    }
    render() {
    const { color, submittedColor } = this.state
    return (
        <div>
            <Form onSubmit={this.handleSubmit}>
                <Form.Group>
                    <Form.Input
                        placeholder='button color'
                        name='color'
                        value={color}
                        onChange={this.handleChange}
                    />
                    <Form.Button content='Submit' />
                </Form.Group>
            </Form>           
                <Button inverted color= {submittedColor}>
                    selected color
                </Button>
            
        </div>
        )
    }
}
export default ButtonExampleInverted;
