import React, { Component } from 'react'
import { Dropdown, Grid, Segment, Form } from 'semantic-ui-react'

const options = [
  { key: 1, text: 'two', value: 2 },
  { key: 2, text: 'three', value: 3 },
  { key: 3, text: 'four', value: 4 },
]

const enteredNumber = 0;

class DropdownExample extends Component {
  state = {value:'', enteredNumber:0}

  handleChange = (e, { value }) => this.setState({ value })

  numberEnteredHandler = (e, { enteredNumber }) => this.setState({ enteredNumber })

  render() {
    const { value } = this.state
    const { enteredNumber } = this.state

    return (
      <Grid columns={1}>
          <Form>
          <Form.Input
              placeholder='number'
              name='number'
              value={enteredNumber}
              onChange={this.numberEnteredHandler}
            />
          </Form>
          
        <Grid.Column>
          <Dropdown
            onChange={this.handleChange}
            options={options}
            placeholder='multiply by'
            selection
            value={value}
          />
        </Grid.Column>
        <Grid.Column>
          <Segment secondary>
            <pre>Current value: {value * enteredNumber}</pre>
          </Segment>
        </Grid.Column>
      </Grid>
    )
  }
}
export default DropdownExample;
