import React, { Component } from 'react'
import { Dropdown, Grid, Segment, Form, Button } from 'semantic-ui-react'

const options = [
    {text: 'one', value: 1 },
    {text: 'two', value: 2 },
    {text: 'three', value: 3 },
    {text: 'four', value: 4 },
]

class DropdownExample extends Component {
  state = {result:'', value: '', enteredNumber: ''}

  handleChange = (e,{value}) => this.setState({value})
  numberEnteredHandler = (e,{enteredNumber }) => this.setState({enteredNumber})
  result = () => {
      return this.setState({result: this.state.value * this.state.enteredNumber})
  }

  render() {
    const { value, enteredNumber, result } = this.state

    return (
      <Grid>
        <Form>
        <Form.Input
              name='number'
              placeholder='number'
              value={enteredNumber}
              onChange={this.numberEnteredHandler}
            />
          <Dropdown
            onChange={this.handleChange}
            options={options}
            placeholder='multiply by'
            selection
            value={value}
          />
          <Segment secondary>
            <pre>Current value:{result}</pre>
          </Segment>
        </Form>
      </Grid>
    )
  }
}
export default DropdownExample;
