import React, { useState } from 'react';
import { Button, Input, Form } from 'semantic-ui-react';

const FormExampleSubmitText = () => {

    const [name, setEnteredName] = useState('');
    const [surname, setEnteredSurname] = useState('');
    
    const [showName, setShowName] = useState('');
    const [showSurname, setShowSurname] = useState('');

    const nameHandler = e => {
        setEnteredName(e.target.value);
    }

    const surnameHandler = e => {
        setEnteredSurname(e.target.value);
    }

    const submitHandler = (e) => {
        setShowName(e.target.value);
        setShowSurname(e.target.value);
    }

return ( 
    <Form onSubmit={submitHandler}>
      <Input
        placeholder='name'
        name='name'
        value={name}
        onChange={nameHandler}
        />
        <Input
        placeholder='surname'
        name='surname'
        value={surname}
        onChange={surnameHandler}
        />
        <Button color='grey' >Submit</Button>
        <p>de hello {name} {surname}</p>
        <br></br>
        <p></p>
    </Form>
    )
}
export default FormExampleSubmitText;
