import React, { Component } from 'react'
import { Dropdown, Grid, Segment, Form } from 'semantic-ui-react'

const options = [
  { key: 1, text: 'One', value: 1 },
  { key: 2, text: 'Two', value: 2 },
  { key: 3, text: 'Three', value: 3 },
]

export default class DropdownExampleControlled extends Component {
  state = {enteredNumber:0}

  handleChange = (e, { value }) => this.setState({value})
  numberHandler = (e, { enteredNumber }) => this.setState({enteredNumber})

  render() {
    const { value } = this.state
    const { enteredNumber } = this.state

    return (
      
      <Grid columns={1}>
        <Form>
        <Form.Input
            placeholder='number'
            name='number'
            value={enteredNumber}
            onChange={this.numberHandler}
            />
          <Dropdown
            onChange={this.handleChange}
            options={options}
            placeholder='Choose an option'
            selection
            value={value}
          />
            <Segment><pre>Current value: {value} and {enteredNumber}</pre></Segment>
        </Form>
      </Grid>
      
    )
  }
}
