import React, { useState } from 'react';
import { Button, Input, Form } from 'semantic-ui-react';

const FormExampleSubmitText = () => {

    const [enteredName, setEnteredName] = useState('');
    const [enteredSurname, setEnteredSurname] = useState('');

    const nameHandler = e => {
        setEnteredName(e.target.value);
    }

    const surnameHandler = e => {
        setEnteredSurname(e.target.value);
    }

    const submitHandler = e => {
        
        const submittedName = {enteredName, enteredSurname};

    }

return ( 
    <Form onSubmit={submitHandler}>
      <Input
        placeholder='name'
        name='name'
        value={enteredName}
        onChange={nameHandler}
        />
        <Input
        placeholder='surname'
        name='surname'
        value={enteredSurname}
        onChange={surnameHandler}
        />
        <Button color='grey' >Submit</Button>
        <p>{enteredName} {enteredSurname}</p>
        <br></br>
        <p>Submitted name: {JSON.stringify(null, 2)}</p>
    </Form>
    )
}
export default FormExampleSubmitText;
