import React, { useState } from 'react';
import { Button, Input, Form } from 'semantic-ui-react';


const ButtonExample = () => {
    
    const [enteredColor, setEnteredColor] = useState('');
    const [color, setColor] = useState('red');

    const colorChangeHandler = (e) => {
        setEnteredColor(e.target.value);
    };

    const changeBtnColor = changedColor => {
        setColor(changedColor);
    };

    return (
        <Form>
          <Input
            placeholder='your favourite color'
            name='colorName'
            value={enteredColor}
            onChange={colorChangeHandler}
            />
            <Button basic color={enteredColor.toLowerCase()}>Colored button</Button>
            <Button onClick={changeBtnColor}>Press me</Button>
            <Button color={changedColor}>:)</Button>
        </Form>
    )
}
export default ButtonExample;
