import React, { Component } from 'react'
import { Dropdown, Grid, Segment, Form } from 'semantic-ui-react'

const options = [
  {text: 'two', value: 2 },
  {text: 'three', value: 3 },
  {text: 'four', value: 4 },
]

class DropdownExample extends Component {
  state = {value: 0, enteredNumber:0}

  handleChange = (e, {value}) => this.setState({value})

  numberEnteredHandler = (e,{ enteredNumber }) => this.setState({ enteredNumber})

  render() {
    const { value } = this.state
    const { enteredNumber } = this.state

    return (
      <Grid columns={1}>
          <Form>
          <Form.Input
              placeholder='number'
              value={enteredNumber}
              onChange={this.numberEnteredHandler}
            />
        <Grid.Column>
          <Dropdown
            onChange={this.handleChange}
            options={options}
            placeholder='multiply by'
            selection
            value={value}
          />
        </Grid.Column>
        <Grid.Column>
          <Segment secondary>
            <pre>Current value:{enteredNumber*value}</pre>
          </Segment>
        </Grid.Column>
        </Form>
      </Grid>
    )
  }
}
export default DropdownExample;
